        email: "",
        password: "",
        successMessage: null
    })
    const redirectToRegister = () => {
        props.updateTitle("Register");
        props.history.push("./register");
    }
    const handleChange = () => {

    }
    return ( < div classNam = "col-12" >
        <
        label > Email Address < /label> <
        input id = "email"
        placeholder = "email"
        value = { state.email }
        onChange = { handleChange }
        / >  <
        label > Password < /label> <
        input type = "password"
        id = "password"
        value = {
